import React, { useEffect, useState } from "react";
import {
  Text,
  View,
  FlatList,
  TextInput,
  StyleSheet,
  TouchableOpacity,
  Image,
  Alert,
} from "react-native";
import { signOut } from "firebase/auth";
import { auth } from "../firebase";
import { db } from "../firebase";
import { collection, onSnapshot, query, where } from "firebase/firestore";
import MaterialCommunityIcons from "react-native-vector-icons/MaterialCommunityIcons";

const Profile = ({ navigation }) => {
  const [profile, setProfile] = useState({});
  const [search, setSearch] = useState("");
  const [friends, setFriends] = useState([]);
  const colRef = query(
    collection(db, "Friends"),
    where("uid", "==", auth.currentUser?.uid)
  );

  const filterData = friends.filter((friend) => {
    return friend.friendName.indexOf(search) >= 0;
  });

  const handleSignOut = () => {
    signOut(auth).then(() => {
      navigation.navigate("Login");
    });
  };

  useEffect(
    () =>
      onSnapshot(
        query(
          collection(db, "users"),
          where("uid", "==", auth.currentUser?.uid)
        ),
        (snapshot) => setProfile(snapshot.docs[0].data())
      ),
    []
  );

  useEffect(
    () =>
      onSnapshot(colRef, (snapshot) =>
        setFriends(snapshot.docs.map((doc) => ({ ...doc.data(), id: doc.id })))
      ),
    []
  );

  const renderFriend = ({ item }) => {
    return (
      <View
        style={{
          flexDirection: "row",
          padding: 20,
          marginBottom: 20,
          backgroundColor: "white",
          borderRadius: 12,
          shadowColor: "#000",
          shadowOffset: { width: 0, height: 10 },
          shadowOpacity: 0.3,
          shadowRadius: 20,
        }}
      >
        <View>
          <Text style={{ fontSize: 22, fontWeight: "700" }}>
            {item.friendName}
          </Text>
          <Text style={{ fontSize: 18, opacity: 0.7 }}>{item.friendRole}</Text>
          <Text style={{ fontSize: 14, opacity: 0.8, color: "#0099cc" }}>
            {item.friendEmail}{" "}
          </Text>
        </View>
      </View>
    );
  };

  const signOutAlert = () => {
    Alert.alert(`Sign Out`, `Are you sure you want to sign out?`, [
      {
        text: "Sign Out",
        onPress: () => handleSignOut(),
      },
      {
        text: "Cancel",
        style: "cancel",
      },
    ]);
  };

  return (
    <View>
      <View style={{ padding: 20 }} />
      <View>
        <Text
          onPress={() => navigation.navigate("MyPosts")}
          style={{ fontSize: 26, fontWeight: "bold" }}
        >
          My Posts
        </Text>
        <View style={styles.line} />
        <View style={styles.container}>
          <View style={styles.border}>
            <Text style={styles.info}>Hello, {profile.name}!</Text>
          </View>
          <View style={styles.border}>
            <Text style={styles.info}>Role: {profile.role}</Text>
          </View>
          <View style={styles.border}>
            <Text style={styles.info}>Email: {profile.email}</Text>
          </View>
        </View>
        <View style={styles.line} />
        <View style={styles.buttonContainer}>
          <TouchableOpacity
            onPress={() => navigation.navigate("EditProfile")}
            style={styles.button}
          >
            <Text style={styles.buttonText}>Edit Profile</Text>
          </TouchableOpacity>
          <TouchableOpacity onPress={signOutAlert} style={styles.button}>
            <Text style={styles.buttonText}>Sign Out</Text>
          </TouchableOpacity>
        </View>
        <View style={styles.divider} />
      </View>
      <View style={styles.buttonContainer}>
        <TouchableOpacity
          onPress={() => navigation.navigate("Friends")}
          style={[styles.button]}
        >
          <Text style={styles.buttonText}>Add/Remove Friend</Text>
        </TouchableOpacity>
        <TextInput
          style={styles.textInput}
          value={search}
          placeholder="Search By Name"
          underlineColorAndroid="transparent"
          onChangeText={(text) => setSearch(text)}
        />
      </View>
      <View style={styles.line} />
      <Text style={styles.header}>My Friends</Text>
      <FlatList
        data={filterData}
        contentContainerStyle={{
          padding: 15,
        }}
        renderItem={renderFriend}
      />
    </View>
  );
};

export default Profile;

const styles = StyleSheet.create({
  line: {
    borderWidth: 1,
    margin: 5,
    opacity: 0.1,
  },
  divider: {
    borderWidth: 1,
    margin: 5,
    opacity: 0.3,
  },
  info: {
    paddingHorizontal: 5,
    paddingVertical: 5,
    fontSize: 25,
  },
  border: {
    borderColor: "white",
    borderWidth: 3,
    marginTop: 5,
    borderRadius: 10,
    shadowColor: "black",
    shadowOffset: { width: 2, height: 6 },
    shadowRadius: 2,
    shadowOpacity: 0.15,
  },
  container: {
    margin: 5,
    alignItems: "center",
  },
  buttonText: {
    fontSize: 20,
  },
  button: {
    backgroundColor: "#0782F9",
    padding: 15,
    borderRadius: 10,
    alignItems: "center",
    alignSelf: "center",
    marginTop: 5,
    margin: 5,
  },
  buttonContainer: {
    flexDirection: "row",
    justifyContent: "space-evenly",
    shadowColor: "black",
    shadowOffset: { width: 4, height: 4 },
    shadowRadius: 2,
    shadowOpacity: 0.2,
  },
  textInput: {
    height: 54,
    width: "40%",
    alignSelf: "center",
    borderWidth: 1,
    borderRadius: 5,
    paddingLeft: 20,
    margin: 5,
    borderColor: "#0782F9",
    backgroundColor: "white",
  },
  header: {
    alignSelf: "center",
    fontSize: 25,
    padding: 7,
    backgroundColor: "#C0C0C0",
    shadowOffset: { width: 4, height: 4 },
    shadowRadius: 2,
    shadowOpacity: 0.2,
  },
});
